mapper.select:
select c1.id ,c1.full_name,c1.company,c1.phone,c1.mphone,v1.`value` source,u1.name owner,v2.value state
FROM tbl_clue c1
LEFT JOIN tbl_dic_value v1
on c1.source = v1.id
join tbl_user u1
on u1.id = c1.`owner`
LEFT join tbl_dic_value v2
on v2.id = c1.state


    c1.full_name like '%${fullName}%'


   and c1.company like '%${company}%'


   and c1.phone = #{phone}


   and c1.mphone = #{mphone}


   and v1.id = #{source}


   and c1.owner = #{owner}


   and v2.id = #{state}


order by c1.full_name
limit #{pageNo},#{pageSize}

select count(*)
FROM tbl_clue c1
LEFT JOIN tbl_dic_value v1
on c1.source = v1.id
join tbl_user u1
on u1.id = c1.`owner`
LEFT join tbl_dic_value v2
on v2.id = c1.state


    c1.full_name like '%${fullName}%'


    and c1.company like '%#{company}%'


    and c1.phone = #{phone}


    and c1.mphone = #{mphone}


    and v1.id = #{source}


    and c1.owner = #{owner}


    and v2.id = #{state}

mapper.delete:
delete from tbl_clue_activity_relation
where activity_id = #{activityId}
and clue_id = #{clueId}

delete from tbl_clue
where id in

  #{id}
